import React, { Component } from 'react'
import { Route, BrowserRouter as Router, Switch, Redirect } from "react-router-dom";
import { connect } from "react-redux";

import SignIn from "../SignIn/SignIn";
import Homepage from "../Homepage/Homepage";
import Leaderboard from "../Leaderboard/Leaderboard";
import AddQuestion from "../AddQuestion/AddQuestion";
import QuestionDetails from "../QuestionDetails/QuestionDetails";
import Poll from "../Poll/Poll";
import NotFound from "../NotFound/NotFound";
import "./App.css";
import { handleInitialData } from '../../actions/shared';

class App extends Component {
  componentDidMount() {
    this.props.dispatch(handleInitialData());   
  }

  render() {
    let qid;
    let intercept = null;

    if (window.location.pathname) {
      qid = window.location.pathname.split("/")[2];
    }

    if (qid) {
      let find = Object.values(this.props.questions).some((question) => {
        return question.id === qid;
      });

      if (!find) {
        intercept = <Route path="/questions"  component={ NotFound } />
      }
    }

    return (
      <div className="wrap">
        <Router>
          <Switch>
            <Route path="/" exact component={ SignIn } />
            <Route path="/homepage" component={ Homepage } />
            <Route path="/leaderboard" component={ Leaderboard } />
            { intercept }
            <Route path="/questions/:question_id" component={ Poll } />
            <Route path="/questions/result/:question_id" component={ Poll } />
            <Route path="/add" component={ AddQuestion } />
            <Route component={ NotFound } />
          </Switch>
        </Router>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
      users: state.users,
      questions: state.questions
  };
}

export default connect(mapStateToProps)(App);
