import React, { Component } from 'react';

import Result from "../Result/Result";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { handleAnswerQuestion } from "./../../actions/questions";
import './QuestionCard.css';

class QuestionCard extends Component {
    render() {
        let bottomPart = null;

        if (!this.props.answered) {
            const url = "/questions/" + this.props.id;

            bottomPart = (
                <div>
                    <div className="option optionOne">
                        <Link onClick={(event) => {
                            console.log(this.props);
                            alert(event.target.textContent);
                            this.props.handleSelect(this.props.id, event.target.textContent);
                        }} className="option-link" to={ url }>Option 1: { this.props.optionOne }</Link>
                    </div>
                    <div className="option optionTwo">
                        <Link className="option-link" to={ url }>Option 2: { this.props.optionTwo }</Link>
                    </div>
                    <p className="hint">Please select one option by clicking on it!</p>
                </div>
            );
        } else {
            bottomPart = <Result optionOne={ this.props.optionOne } 
                                optionTwo={ this.props.optionTwo } 
                                countOptionOne={ this.props.countOptionOne }
                                countOptionTwo={ this.props.countOptionTwo } />
        }

        return <div className="question-card" >
            <h3>Author: { this.props.authedUser }</h3>
            {bottomPart}
        </div>
    }
};


const mapStateToProps = (state) => {
    return {
        users: state.users,
        questions: state.questions,
        username: state.authedUser.username || ""
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        handleSelect: (questionId, answer) => {
            dispatch(handleAnswerQuestion(questionId, answer));
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(QuestionCard);
