import React from 'react';

import './Result.css';

const Result = (props) => {
    const sum = parseInt(props.countOptionOne)
        + parseInt(props.countOptionTwo);
    const computePercentage = (option) => {
        return (option * 100) / sum;
    }

    return <div className="result" >
        <h2>Current Poll Result</h2>
        <div className="optionResult">
            <div className="question">{ props.optionOne }</div>
            <div><b>Votes:</b> { props.countOptionOne } - <b>Percentage:</b> {computePercentage(props.countOptionOne)}%</div>
        </div>
        <div className="optionResult">
            <div className="question">{ props.optionTwo }</div>
            <div><b>Votes:</b> { props.countOptionTwo } - Percentage: {computePercentage(props.countOptionTwo)}%</div>
        </div>
    </div>
};

export default Result;
