import React from 'react';

import Result from "../Result/Result";
import './QuestionCard.css';

const QuestionCard = (props) => {
    let bottomPart = null;

    if (!props.answered) {
        bottomPart = (
            <div>
                <p className="option optionOne">Option 1: { props.optionOne }</p>
                <p className="option optionTwo">Option 2: { props.optionTwo }</p>
                <p className="hint">Please select one option by clicking on it!</p>
            </div>
        );
    } else {
        bottomPart = <Result optionOne={ props.optionOne } optionTwo={ props.optionTwo } />
    }

    return <div className="question-card" >
        <h3>Author: { props.authedUser }</h3>
        {bottomPart}
    </div>
};

export default QuestionCard;
