import { AUTHENTICATE_USER, SIGN_OUT } from "../actions/types";
import { RECEIVE_USERS } from "../actions/types";
import { ADD_QUESTION, RECEIVE_QUESTIONS } from "../actions/types";

export default function Reducer (state = {}, action) {
    switch (action.type) {
        case AUTHENTICATE_USER:
            return {
                ...state,
                username: action.username
            };
        case SIGN_OUT:
            return {}
        case RECEIVE_QUESTIONS:
            return {
                ...state,
                ...action.questions
            }
        case ADD_QUESTION:
            return {
                ...state,
                [ action.question.id ]: action.question
            }
        case RECEIVE_USERS:
            return {
                ...state,
                ...action.users
            };
        default: 
            return state;
    }
}
