import React from 'react';
import { connect } from "react-redux";

import './Result.css';

const Result = (props) => {
    const sum = parseInt(props.countOptionOne)
        + parseInt(props.countOptionTwo);

    const computePercentage = (option) => {
        return (option * 100) / sum;
    }

    console.log(props);

    return <div className="result" >
        <h2>Current Poll Result</h2>
        <div className="optionResult">
            <div className="question">{ props.optionOne }</div>
            <div>
                <b>Votes:</b> { props.countOptionOne } - <b>Percentage: </b> 
                    {computePercentage(props.countOptionOne)}%
            </div>
        </div>
        <div className="optionResult">
            <div className="question">{ props.optionTwo }</div>
            <div>
                <b>Votes:</b> { props.countOptionTwo } - <b>Percentage: </b> 
                    {computePercentage(props.countOptionTwo)}%
            </div>
        </div>
    </div>
};

const mapStateToProps = (state) => {
    return {
        users: state.users,
        questions: state.questions,
        username: state.authedUser.username || ""
    };
}

export default connect()(Result);
