import React from 'react';

import Result from "../Result/Result";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { handleAnswerQuestion } from "./../../actions/questions";
import './QuestionCard.css';

const QuestionCard = (props) => {
    let bottomPart = null;

    if (!props.answered) {
        const url = "/questions/" + props.id;

        bottomPart = (
            <div>
                <div className="option optionOne">
                    <Link onClick={() => {
                        console.log("xxx");
                    }} className="option-link" to={ url }>Option 1: { props.optionOne }</Link>
                </div>
                <div className="option optionTwo">
                    <Link className="option-link" to={ url }>Option 2: { props.optionTwo }</Link>
                </div>
                <p className="hint">Please select one option by clicking on it!</p>
            </div>
        );
    } else {
        bottomPart = <Result optionOne={ props.optionOne } 
                             optionTwo={ props.optionTwo } 
                             countOptionOne={ props.countOptionOne }
                             countOptionTwo={ props.countOptionTwo } />
    }

    return <div className="question-card" >
        <h3>Author: { props.authedUser }</h3>
        {bottomPart}
    </div>
};


const mapStateToProps = (state) => {
    return {
        users: state.users,
        questions: state.questions,
        username: state.authedUser.username || ""
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        handleClick: (questionId, answer) => {
            dispatch(handleAnswerQuestion(questionId, answer));
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(QuestionCard);
