import React, { Component } from 'react';
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";

import QuestionCard from "./../QuestionCard/QuestionCard";
import "./Homepage.css";

class Homepage extends Component {
  state = {
    answered: false
  }

  handleClick = () => {   
    this.setState(() => {
      return {
        answered: !this.state.answered
      }
    });   
  }

  render() {
    if (!this.props.username) {
        return <Redirect to="/" />
    }

    const kindOfQuestion = this.props.match.params.kindof;
    let questions;

    if (kindOfQuestion === "answered") {
        questions = [];
    } else {
        const arrayQuestions = Object.values(this.props.questions);

        questions = arrayQuestions.map((question) => {          
            return <QuestionCard author={question.author} 
                                optionOne={question.optionOne.text}
                                optionTwo={question.optionTwo.text} />
        });
    }

    console.log(questions);

    return (
      <div>
        <h1>HOMEPAGE</h1>
        <a href="#" id="toggle" onClick={this.handleClick}
                    className="ghost-button">Toggle Un-/Answered Questions</a>
        {questions}
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.users,
    questions: state.questions,
    username: state.authedUser.username || ""
  };
}

export default connect(mapStateToProps)(Homepage);
