import React, { Component } from 'react';
import { connect } from "react-redux";

import "./AddQuestion.css";

class AddQuestion extends Component {
  render() {
    if (!this.props.username) {
        return <Redirect to="/" />
    }

    return (
      <div>
        <h1>Would you rather ... ?</h1>
        <input type="text" id="optionOne" placeholder="Enter here the first option ..." />
        <input type="text" id="optionTwo" placeholder="Enter here the second option ..." />
        <button>Insert question</button>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
    return {
        username: state.authedUser.username
    };
}

export default connect(mapStateToProps)(AddQuestion);
