import React, { Component } from 'react';
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";

import Navbar from "./../Navbar/Navbar";
import QuestionCard from "./../QuestionCard/QuestionCard";
import './Poll.css';

class Poll extends Component {

    render() {
        let authed = false; 

        if (document.cookie) {
        const parts = document.cookie.split("=");

        if (parts.length) {
            authed = parts[1];
        }
        }

        if (!this.props.username && !authed) {
            return <Redirect to="/" />
        }

        let qid;

        if (this.props.questionId) {
            qid = this.props.questionId;
        } else {
            qid = window.location.pathname.split("/")[2];
        }

        const currentQuestion = this.props.questions[qid];

        let avatarUrl;

        if (this.props.users[currentQuestion.author]) {
            avatarUrl = this.props.users[currentQuestion.author].avatarURL;
        }

        return <div className="result" >
                    <Navbar />
                    <h1>Would you rather ... ?</h1> 
                    <QuestionCard author={ currentQuestion.author } key={ qid }
                            authedUser={ this.props.username }
                            optionOne={ currentQuestion.optionOne.text }
                            optionTwo={ currentQuestion.optionOne.text }
                            answered="false"
                            countOptionOne={ currentQuestion.optionOne.votes.length }
                            countOptionTwo={ currentQuestion.optionTwo.votes.length }
                            id={ qid }
                            avatarUrl={ avatarUrl }
                            />         
                </div>
    }
};

const mapStateToProps = (state) => {
    return {
        questions: state.questions,
        users: state.users,
        username: state.authedUser.username || ""
    };
}

export default connect(mapStateToProps)(Poll);
