import React, { Component } from 'react';
import { connect } from "react-redux";
import { authenticateUser } from "../actions/authedUser";
import { Link, Redirect } from "react-router-dom";

import "./style.css";

class Login extends Component {
  state = {
    isAuthentificated: false
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const { username } = this.state;
    const { dispatch } = this.props;

    if (username !== "") {

      dispatch(authenticateUser(username));
    }
  }

  handleClick = (event) => {
    const name = event.target.textContent;

    this.props.dispatch(authenticateUser(name)); 
    this.setState(() => {
      return {
        isAuthentificated: true
      }
    });   
  }

  render() {
    const { from } = this.props.location.state || { from: {
      pathname: "/"
    }};
    const { isLogged } = this.state;

    if (this.props.username) {
      return <Redirect to={ from } />
    }

    const listItems = Object.values(this.props.users).map((user) => {
      return <li onClick={ this.handleChange } key={user.id} id={user.id}>{ user.name }</li>
    });

    return (
      <div>
        <ul className="users-list">
          {listItems}
        </ul>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.users,
    username: state.username || ""
  };
}

export default connect(mapStateToProps)(Login);
