import { AUTH_USER, SIGN_OUT } from "../actions/types";

export default function auth (state = {}, action) {
    switch (action.type) {
        case AUTH_USER:
            return {
                ...state,
                username: action.username
            };
        case SIGN_OUT:
            return {}
        default: 
            return state;
    }
}
